package com.example.spaces;import androidx.annotation.NonNull;import androidx.annotation.Nullable;import androidx.appcompat.app.AppCompatActivity;import androidx.fragment.app.Fragment;import android.os.Bundle;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.Button;import android.widget.EditText;import android.widget.TextView;public class SubTabNumberSteps extends Fragment {    EditText persons_height_SubTabNumberSteps;    EditText number_of_steps_SubTabNumberSteps;    Button button_SubTabNumberSteps;    TextView output_SubTabNumberSteps;    @Nullable    @Override    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {        return inflater.inflate(R.layout.activity_sub_tab_number_steps, container, false);    }    @Override    public void onActivityCreated(@Nullable Bundle savedInstanceState) {        super.onActivityCreated(savedInstanceState);        persons_height_SubTabNumberSteps = getActivity().findViewById(R.id.persons_height_SubTabNumberSteps);        number_of_steps_SubTabNumberSteps = getActivity().findViewById(R.id.number_of_steps_SubTabNumberSteps);        button_SubTabNumberSteps = getActivity().findViewById(R.id.button_SubTabNumberSteps);        output_SubTabNumberSteps = getActivity().findViewById(R.id.output_SubTabNumberSteps);        button_SubTabNumberSteps.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                if (!persons_height_SubTabNumberSteps.getText().toString().equals("") && !number_of_steps_SubTabNumberSteps.getText().toString().equals("")) {                    double persons_height = Double.parseDouble(persons_height_SubTabNumberSteps.getText().toString());                    double number_of_steps = Double.parseDouble(number_of_steps_SubTabNumberSteps.getText().toString());                    double output = (number_of_steps*100)/(100/(((persons_height/4)+37)/100));                    String stringOutput = output+"";                    Float floatOutput = Float.parseFloat(stringOutput);                    output_SubTabNumberSteps.setText(floatOutput+"");                }            }        });    }}